package multicast.chat.app;

import multicast.chat.list.LinkedList;

import static multicast.chat.utilities.StringUtils.join;
import static multicast.chat.utilities.StringUtils.split;
import static multicast.chat.app.MessageUtils.getMessage;

import org.apache.commons.text.WordUtils;

public class App {
    public static void main(String[] args) {
        LinkedList tokens;
        tokens = split(getMessage());
        String result = join(tokens);
        System.out.println(WordUtils.capitalize(result));
    }
}
